package com.example.gran_centre;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import android.widget.ImageView;

import com.bumptech.glide.Glide;

public class PeliculasActivity extends AppCompatActivity {
    ImageView imgFilm1, imgFilm2, imgFilm3, imgFilm4, imgFilm5;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_peliculas);

        //Afegir img holder:
        imgFilm1 = findViewById(R.id.film1);
        imgFilm2 = findViewById(R.id.film2);
        imgFilm3 = findViewById(R.id.film3);
        imgFilm4 = findViewById(R.id.film4);
        imgFilm5 = findViewById(R.id.film5);

        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .error(R.drawable.ic_error_foreground) // Puedes establecer una imagen de error si la carga falla
                .into(imgFilm1);

        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .error(R.drawable.ic_error_foreground) // Puedes establecer una imagen de error si la carga falla
                .into(imgFilm2);

        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .error(R.drawable.ic_error_foreground) // Puedes establecer una imagen de error si la carga falla
                .into(imgFilm3);

        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .error(R.drawable.ic_error_foreground) // Puedes establecer una imagen de error si la carga falla
                .into(imgFilm4);

        Glide.with(this)
                .load("https://cineciutat.org/storage/app/uploads/public/652/f18/f7d/thumb_3806_314_443_0_0_crop.jpg")
                .error(R.drawable.ic_error_foreground) // Puedes establecer una imagen de error si la carga falla
                .into(imgFilm5);
    }
}